function mapDispatchToProps(dispatch) {
  return {
    openModalClick: function(props) {
        dispatch({
          type: 'openModal',
          clickOnTattoo:props.isOpen,
          favTattooPhotoLink:props.tattooPhotoLink,
          artistId:props.artistId,
          idPhotoSelected:props.idPicture,
