//Alimente le modal envoyé au tatoueur depuis le UserPage/FavTattoosProfile et depuis TattooModal/TattooArtistCardModal

import React from 'react';

//material UI
import {
  Step,
  Stepper,
  StepLabel,
  StepContent,
} from 'material-ui/Stepper';
import TextField from 'material-ui/TextField';
import RaisedButton from 'material-ui/RaisedButton';
import FlatButton from 'material-ui/FlatButton';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'

//reactstrap
import { Modal, ModalHeader, ModalBody, Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

import SelectField from 'material-ui/SelectField';
import MenuItem from 'material-ui/MenuItem';



/**
 * Vertical steppers are designed for narrow screen sizes. They are ideal for mobile.
 *
 * To use the vertical stepper with the contained content as seen in spec examples,
 * you must use the `<StepContent>` component inside the `<Step>`.
 *
 * <small>(The vertical stepper can also be used without `<StepContent>` to display a basic stepper.)</small>
 */

 // class ModalProjectForm extends Component{
 //   render(){
 //     return(
 //
 //   )}
 // };



class ProjectForm extends React.Component {
 constructor(props){
   super(props);
   this.handleNext = this.handleNext.bind(this);
   this.handlePrev = this.handlePrev.bind(this);
   this.handleChange = this.handleChange.bind(this);
   this.renderStepActions = this.renderStepActions.bind(this);
   this.toggle = this.toggle.bind(this);
   this.state ={
     modal: false,
     finished: false,
     stepIndex: 0,
     description: "",
     phone:"",
     availability:"",
     value: 1
   }
 }

 toggle() {
   //if(this.props.clickToSend && this.state.modal !== this.props.clickToSend){
     this.setState({
       modal: !this.state.modal
     });
   // }else{
   //   this.setState({
   //     modal: false
   //   });
   // }

 }
 // componentWillMount(){
 //   this.setState({
 //     modal: false,
 //   });
 // }

//  componentDidMount(){
//    this.setState({
//      modal: this.state.modal,
//      stepIndex: 0,
//      description: "",
//      phone:"",
//      availability:"",
//      value: 1
//    });
//  }
//
componentDidUpdate(prevProps){
  if(this.props.clickToSend !==prevProps.clickToSend && this.props.clickToSend== true){
    this.setState({
      modal: this.state.modal
    })
  }
}

 handleChange(event,index, value) {
     this.setState({
       [event.target.name]: event.target.value,
       value: value
     });
   }

  handleNext(){
    this.setState({
      stepIndex: this.state.stepIndex + 1,
      finished: this.state.stepIndex >= 2,
    });
  };

  handlePrev(){
    if (this.state.stepIndex > 0){
      this.setState({
        stepIndex: this.state.stepIndex - 1
      });
    }
  };

  renderStepActions(step) {
    const {stepIndex} = this.state;

    return (
      <div style={{margin: '12px 0'}}>
        <RaisedButton
          label={stepIndex === 2 ? 'Envoyer' : 'Suivant'}
          disableTouchRipple={true}
          disableFocusRipple={true}
          primary={true}
          onClick={this.handleNext}
          style={{marginRight: 12}}
        />
        {step > 0 && (
          <FlatButton
            label="Précédent"
            disabled={stepIndex === 0}
            disableTouchRipple={true}
            disableFocusRipple={true}
            onClick={this.handlePrev}
          />
        )}
      </div>
    );
  }

  render() {
    const {finished, stepIndex, description, phone, availability} = this.state;
    // console.log("this.state.modal",this.state.modal);
    // console.log("this.props.clickToSend",this.props.clickToSend)

    return (
  <MuiThemeProvider>
    {/* <Button color="danger" onClick={this.toggle}>Formulaire</Button> */}
    <div style={{maxHeight: 400, margin: 'auto'}}>
    <Modal isOpen={this.state.modal} toggle={this.toggle}>
      <ModalHeader toggle={this.toggle}></ModalHeader>
      <ModalBody>

        <Stepper activeStep={stepIndex} orientation="vertical">
          <Step>
            <StepLabel className="stepLabelForm">Décrivez votre projet</StepLabel>
            <StepContent>
              <TextField
                name="description"
                hintText="ex : une fleur en dot sur l'omoplate"
                multiLine={true}
                floatingLabelText="Parlez nous de votre projet"
                rows={2}
                rowsMax={3}
                fullWidth={true}
                onChange={this.handleChange}
                value={this.state.description}
              />
              {this.renderStepActions(0)}
            </StepContent>
          </Step>
          <Step>
            <StepLabel className="stepLabelForm">Pour être contacté</StepLabel>
            <StepContent>
              <TextField
                name="phone"
                floatingLabelFixed="Votre numéro de téléphone"
                floatingLabelText="Laissez votre numéro au tatoueur"
                hintText="(+33)6 61 23 45 67"
                fullWidth={true}
                onChange={this.handleChange}
                value={this.state.phone}
              />
            {this.renderStepActions(1)}
            </StepContent>
          </Step>
          <Step>
            <StepLabel className="stepLabelForm">Vous préférez être contacté</StepLabel>
            <StepContent>
              <SelectField
                  name="availability"
                  fullWidth={true}
                  onChange={this.handleChange}
                  value={this.state.value}
                >
                  <MenuItem value={1} primaryText="Définissez un créneau" />
                  <MenuItem value={2} primaryText="midi (entre 12h et 14h)" />
                  <MenuItem value={3} primaryText="après-midi (entre 14h et 17h)" />
                  <MenuItem value={4} primaryText="soir (entre 17h et 19h)" />
              </SelectField>
              {this.renderStepActions(2)}
            </StepContent>
          </Step>

        </Stepper>
        {finished && (
          <p style={{margin: '20px 0', textAlign: 'center'}}>
            Votre demande a bien été envoyée !
            {/* <a
              href="#"
              onClick={(event) => {
                event.preventDefault();
                this.setState({stepIndex: 0, finished: false});
              }}
            >
              <br/>
            Cliquez ici
            </a> pour envoyer une nouvelle demande */}
          </p>
            )}

      </ModalBody>
    </Modal>
    </div>
  </MuiThemeProvider>
    );
  }
}

export default ProjectForm;
