import React from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input, FormText, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

export default class SigninForm extends React.Component {
  constructor(props) {
   super(props);
   this.state = {
     modal: false,
     name:'',
     firstName:'',
     email: '',
     password:'',
     confirmpassword:'',
     messageError:''
   };

   this.toggle = this.toggle.bind(this);
   this.handleChange = this.handleChange.bind(this);
   this.handleSubmit = this.handleSubmit.bind(this);
   //this.handleSignIn=this.handleSignIn.bind(this);
 }

 toggle() {
   this.setState({
     modal: !this.state.modal
   });
 }

 handleChange(event) {
     this.setState({value: event.target.value});
   }

handleSubmit(event) {
       alert('A name was submitted: ' + this.state.value);
       event.preventDefault();
     }

 //handleSignIn =() =>{
 //   fetch("http://127.0.0.1:3000/signup"),
 //   { method :"POST",
 //     headers: {'Content-Type':'application/x-www-form-urlencoded'},
 //     body: "firstName="+this.state.firstName+"&name="+ this.state.name+"&email= "+this.state.email+"&password="+this.state.password
 //   }
 //   .then((response)=> response.json())
 //   .then((user)=> {
 //      console.log(user);
 //     }
 //   )
 //   .catch((error)=> console.log(error));
 // }

  render() {
    console.log("name", this.state.name)
    return (
<div>
  <Button color="danger" onClick={this.toggle}>Créer un compte</Button>
       <Modal isOpen={this.state.modal} toggle={this.toggle}>
                  <Form toggle={this.toggle}>
                    <Row form>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="name">Votre nom</Label>
                        <Input type="text" name="name" id="name" value={this.state.value} onChange={this.handleChange} />
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="firsName">Votre prénom</Label>
                        {/* <Input type="text" name="firsName" id="firsName" onChange={(event) => this.setState({firstName: event.target.firstName})} /> */}
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="email">Votre email</Label>
                        {/* <Input type="email" name="email" id="email" onChange={(event) => this.setState({email: event.target.email})} /> */}
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="Password">Créez un mot de passe</Label>
                        {/* <Input type="password" name="password" id="password" onChange={(event) => this.setState({password: event.target.password})} /> */}
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="Password">Confirmez votre mot de passe</Label>
                        {/* <Input type="password" name="confirmpassword" id="confirmpassword" onChange={(confirmpassword) => this.setState({confirmpassword: confirmpassword})} /> */}
                        </FormGroup>
                      </Col>
                    </Row>
                    <Button onClick={this.handleSignIn}>Sign in</Button>
                </Form>
       </Modal>
</div>





    );
  }
}
