import React from 'react';
import { FormGroup, Label, Input } from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.css';
import '../Stylesheets/ProjectForm.css';

import TextField from 'material-ui/TextField';
import SelectField from 'material-ui/SelectField';
import MenuItem from 'material-ui/MenuItem';
import {
  Step,
  Stepper,
  StepLabel,
  StepContent,
  StepButton } from 'material-ui/Stepper';
import RaisedButton from 'material-ui/RaisedButton';
import FlatButton from 'material-ui/FlatButton';

import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'


export default class ProjectForm extends React.Component {
  constructor(){
    super();
    this.handleNext = this.handleNext.bind(this);
    this.handlePrev = this.handlePrev.bind(this);
    this.getStepContent = this.getStepContent.bind(this);
    this.setState({
      finished: false,
      stepIndex: 0,
    })
  }


    handleNext(){
      if (this.state.stepIndex < 2) {
        this.setState({
          stepIndex: this.state.stepIndex + 1,
          finished: this.state.stepIndex >= 2
        });
      }
    };

    handlePrev () {
      if (this.state.stepIndex > 0) {
        this.setState({stepIndex: this.state.stepIndex - 1});
      }
    };

    handleClick(){
      this.setState({
        stepIndex: 0
      })
    }

    getStepContent(stepIndex) {
    switch (this.state.stepIndex) {
      case 0:
        return 'Select campaign settings...';
      case 1:
        return 'What is an ad group anyways?';
      case 2:
        return 'This is the bit I really care about!';
      default:
        return 'You\'re a long way from home sonny jim!';
    }
  }

  render() {
    return (
      <MuiThemeProvider>
          <Stepper
            activeStep={this.state.stepIndex}
            linear={false}
            orientation="vertical">
          <Step>
              <StepLabel>Select campaign settings</StepLabel>
            <StepButton onClick={this.handleClick}>
                 Message à l'attention des tatoueurs
               </StepButton>
               <StepContent>
                 <TextField
                   hintText="ex : une fleur en dot sur l'omoplate"
                   multiLine={true}
                   rows={2}
                   rowsMax={3}
                   fullWidth={true}
                 /><br />
                 {this.renderStepActions(0)}
               </StepContent>
             </Step>
             {/* Avoir si ça marche */}
             <div>
          {this.state.finished ? (
            <p>
              <a
                href="#"
                onClick={(event) => {
                  event.preventDefault();
                  this.setState({stepIndex: 0, finished: false});
                }}
              >
                Click here
              </a> to reset the example.
            </p>
          ) : (
            <div>
              <p>{this.getStepContent(stepIndex)}</p>
              <div style={{marginTop: 12}}>
                <FlatButton
                  label="Back"
                  disabled={stepIndex === 0}
                  onClick={this.handlePrev}
                  style={{marginRight: 12}}
                />
                <RaisedButton
                  label={stepIndex === 2 ? 'Finish' : 'Next'}
                  primary={true}
                  onClick={this.handleNext}
                />
              </div>
            </div>
          )}
        </div>


             <Step>
                  <StepButton onClick={() => this.setState({stepIndex: 0})}>
                    Un numéro de téléphone pour vous contacter
                  </StepButton>
                  <StepContent>
                    <TextField
                      hintText="ex : une fleur en dot sur l'omoplate"
                      multiLine={true}
                      rows={2}
                      rowsMax={3}
                      fullWidth={true}
                    /><br />
                    {this.renderStepActions(0)}
                  </StepContent>
                </Step>

            {/* <div>Décrivez votre projet de tatouage</div> */}
            <TextField
              hintText="ex : une fleur en dot sur l'omoplate"
              multiLine={true}
              rows={2}
              rowsMax={3}
              fullWidth={true}
            /><br />
            <TextField
              hintText="ex : une fleur en dot sur l'omoplate"
              multiLine={true}
              rows={2}
              rowsMax={3}
              fullWidth={true}
            /><br />
            <SelectField
                floatingLabelText="Frequency"
                fullWidth={true}
              >
                <MenuItem value={1} primaryText="Never" />
                <MenuItem value={2} primaryText="Every Night" />
                <MenuItem value={3} primaryText="Weeknights" />
                <MenuItem value={4} primaryText="Weekends" />
                <MenuItem value={5} primaryText="Weekly" />
              </SelectField>
              <br />


            <TextField
              floatingLabelFixed="Votre numero de téléphone"
              hintText="(+33)6 61 23 45 67"
              type="number"
              fullWidth={true}
            /><br />
        </Stepper>
    </MuiThemeProvider>
    )
  }
}

