//Alimente UserPage

import React from 'react';
import {Card, CardTitle, CardImg, CardSubtitle, Button, Container, Row, Col} from 'reactstrap';

import TattooArtistCardModal from '../Components/TattooArtistCardModal.js'
import 'bootstrap/dist/css/bootstrap.css';
import '../Stylesheets/FavArtistsProfile.css';


import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart } from '@fortawesome/free-solid-svg-icons'

import {connect} from 'react-redux';


class FavArtistsProfile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      artistsList: []
    }
  }

  componentDidMount() {
    var ctx = this;
    fetch("http://localhost:3000/user?user_id=" + this.props.userId)
    .then(function(response) {
      return response.json();
    }).then(function(data) {
      var artistsListCopy = [...ctx.state.artistsList];
      var userFavoriteArtist = data.result.userFavoriteArtist

        userFavoriteArtist.map(function(favArtists) {
          return artistsListCopy.push(favArtists)
        })
      ctx.setState({artistsList: artistsListCopy});
    }).catch(function(error) {
      console.log('Request failed', error);
    });
  }



  render() {
    // var artistsList = [];
    // artistsList.push({src: '../avatarsTatoueurs/11201563_749803451831654_737090053_a.jpg', artistName: "Bichon", tattooShop: "Golden Rabbit"});
    // artistsList.push({src: '../avatarsTatoueurs/41450515_1897257143642841_5668628696324374528_n.jpg', artistName: "Princess Madness", tattooShop: "Lez'Art du corps"});

    // var artistsDisplayedCards = this.state.artistsList.map(function(tattoo, i) {
    //   return <ArtistCard key={i} artistName={tattoo.artistNickname} artistImage={tattoo.artistPhotoLink} tattooShop={tattoo.artistCompanyName}/>
    // })

    return (<div className="containerArtist col-12">
      <ArtistCard/>
      {/* {artistsDisplayedCards} */}
    </div>);
  }
}

class ArtistCard extends React.Component {
  render() {
    return (
      <Card id="artistInfoBoxModal">
        <CardBody>
          <Row>
            <Col xs="4" id="artistInfoLeftBoxModal">
              <img src="https://res.cloudinary.com/crazycloud/image/upload/v1542304276/gas3khshphtf0rs3w0oj.jpg" alt="Card image cap" id="artistImageModal" />
              <div id="artistStarRateModal">
                <FontAwesomeIcon icon={faStar} className="artistStarIconModal fa-xs"/>
                <FontAwesomeIcon icon={faStar} className="artistStarIconModal fa-xs"/>
                <FontAwesomeIcon icon={faStar} className="artistStarIconModal fa-xs"/>
                <FontAwesomeIcon icon={faStar} className="artistStarIconModal fa-xs"/>
                <FontAwesomeIcon icon={faStar} className="artistStarIconModal fa-xs"/>
              </div>
              <Button outline color="success" size="sm" className="artistButtonModal" ><FontAwesomeIcon icon={faHeart} className="fa-xs"/> Garder</Button>
            <Button outline color="success" size="sm" className="artistButtonModal" onClick={this.handleClickSend}> <ProjectForm clickToSend={this.state.clickToSend}/> <FontAwesomeIcon icon={faEnvelope} className="fa-xs"/> Contacter</Button>
            </Col>
            <Col xs="8">
              <CardTitle id="artistNameModal">BICHON</CardTitle>
              <CardSubtitle id="artistCompanyNameModal">The Golden Rabbit Tattoo</CardSubtitle>
              <CardText>Bichon tatoue depuis 10 ans. Il a commencé à l'âge de 14 ans avec sa grande soeur. Depuis il est passionné de tatouages.</CardText>
              <div id = "artistAllBadgeModal">
                <Row>
                  <h6><Badge color="info" pill className="artistStyleBadgeModal">Black & Grey</Badge></h6>
                  <h6><Badge color="info" pill className="artistStyleBadgeModal">Japonais</Badge></h6>
                  <h6><Badge color="info" pill className="artistStyleBadgeModal">Fine Line</Badge></h6>
                </Row>
              </div>
              <CardText><FontAwesomeIcon icon={faMapMarkerAlt} /> 16 Rue Geoffroy-Marie, 75009 Paris</CardText>
            </Col>
          </Row>
        </CardBody>
      </Card>
    );
  }
}

function mapStateToProps(store) {
  return { userId: store.user._id,
  }
}

export default connect(
    mapStateToProps,
    null
)(FavArtistsProfile);
