import React from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input, FormText, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

export default class SigninForm extends React.Component {
  constructor(props) {
   super(props);
   this.state = {
     modal: false,
     name:'',
     firstName:'',
     email: '',
     password:'',
     confirmpassword:'',
     messageError:''
   };

   this.toggle = this.toggle.bind(this);
   this.handleSignIn=this.handleSignIn.bind(this);
 }

 toggle() {
   this.setState({
     modal: !this.state.modal
   });
 }

 handleSignIn(){
   fetch("http://127.0.0.1:3000/signin"),
   { method :"POST",
     headers: {'Content-Type':'application/x-www-form-urlencoded'},
     body: "firstName="+this.state.firstName+"&name="+ this.state.name+"&email= "+this.state.email+"&password="+this.state.password
   }
   .then((response)=> response.json())
   .then((user)=> {
      console.log(user);
     }
   )
 }

  render() {
    return (
<div>
  <Button color="danger" onClick={this.toggle}>Créer un compte</Button>
       <Modal isOpen={this.state.modal} toggle={this.toggle}>
                  <Form toggle={this.toggle}>
                    <Row form>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="name">Votre nom</Label>
                        <Input type="text" name="name" id="name" onChange={(name) => this.setState({name: name})} />
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="firsName">Votre prénom</Label>
                        <Input type="text" name="firsName" id="firsName" onChange={(firstName) => this.setState({firstName: firstName})} />
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="email">Votre email</Label>
                        <Input type="email" name="email" id="email" onChange={(email) => this.setState({email: email})} />
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="Password">Créez un mot de passe</Label>
                        <Input type="password" name="password" id="password" onChange={(password) => this.setState({password: password})} />
                        </FormGroup>
                      </Col>
                      <Col md={6}>
                        <FormGroup>
                          <Label for="Password">Confirmez votre mot de passe</Label>
                        <Input type="password" name="confirmpassword" id="confirmpassword" onChange={(confirmpassword) => this.setState({confirmpassword: confirmpassword})} />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Button onClick={this.handleSignIn}>Sign in</Button>
                </Form>
       </Modal>
</div>





    );
  }
}
