import React, {Component} from 'react';
import { ScrollTo } from "react-scroll-to";
import { Link } from 'react-router-dom';
import AuthForm from '../Components/AuthForm.js';
import GalleryPage from './GalleryPage.js'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronCircleDown } from '@fortawesome/free-solid-svg-icons'

import '../Stylesheets/HomePage.css';


class HomePage extends Component {


  render(){
    return (

    <div className="container-homePageContainer">
      <div className="text-accueil">
        <h1 className="findMyTattooTitle">Find My Tattoo</h1>
        <h2>vous aide à construire votre projet et à trouver le tatoueur ou la tatoueuse idéal(e)</h2>
      </div>


      <ScrollTo>
       {({ scrollTo }) => (
         <a href="/" onClick={() => scrollTo({ref: this.myGalleryRef, y: 830, smooth: true})}><FontAwesomeIcon icon={faChevronCircleDown} className="scrollTo"/></a>
       )}
      </ScrollTo>
      <GalleryPage ref={this.myGalleryRef} />
      {/* <Link to={'/#buttonForGallery'}> <FontAwesomeIcon icon={faChevronCircleDown} id="buttonForGallery" className="scrollTo"/> </Link> */}
    </div>
  )}
}

export default HomePage;
